    mounted () {
      this.$nextTick(() => {
        if (!this.tl) {
          this.tl = new TimelineMax({
            paused: true
          })

          if (this.tablet) {
            this.tl.to(this.$refs.building, 1, { x: '-=70', ease: Power3.easeOut }, 0.125)
